partials.components_anchored_menu:
{{#with (default menuId (concat "menu-" (random 1000 9999))) as |menuId|}}
<div class="mdc-menu-surface--anchor {{../anchorClasses}}">
  <button class="mdc-button {{../buttonClasses}}" id="{{menuId}}-trigger">
    {{#if ../imageUrl ~}}
      <img src="{{../imageUrl}}" class="{{../imageClasses}}" />
    {{/if}}
    {{#if ../iconClasses}}
      <span class="{{../iconClasses}}"></span>
    {{/if ~}}
    {{{../buttonLabel}}}
    {{#if ../arrowClasses}}
      <span class="{{../arrowClasses}}"></span>
    {{/if}}
  </button>
  <div class="mdc-menu mdc-menu-surface {{../menuClasses}}" tabindex="-1" id="{{menuId}}" data-menu-trigger="#{{menuId}}-trigger">
    <ul class="mdc-menu__items mdc-list {{../menuListClasses}}" role="menu" aria-hidden="true">
      {{> @partial-block}}
    </ul>
  </div>
</div>
{{/with}}

partials.components_horizontal_card_scroller:
{{#ifAll (compare (length items) "===" 0) @partial-block}}
  {{> @partial-block}}
{{else}}
  <div class="mdc-layout-grid equal-height {{outerClasses}}">
    {{#if (any items)}}
    <div class="scroll-button scroll-left {{scrollButtonClasses}} {{scrollButtonLeftClasses}}">
      <span class="scroll-icon {{scrollIconClasses}} {{scrollLeftIconClasses}}"></span>
    </div>
    <div class="scroll-button scroll-right {{scrollButtonClasses}} {{scrollButtonRightClasses}}">
      <span class="scroll-icon {{scrollIconClasses}} {{scrollRightIconClasses}}"></span>
    </div>
    {{/if}}
    <div class="mdc-layout-grid__inner scrolling-grid {{innerClasses}}">
      {{#if (any items)}}
        {{#each items}}
          <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone {{../itemClasses}}">
            {{> (lookup ../this 'itemsPartial')}}
          </div>
        {{/each}}
        {{#if moreUrl}}
          <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone latest-content-primary last-card {{moreClasses}}">
            <div class="mdc-card align-centre mdc-typography--body1 {{moreCardClasses}}">
              <a href="{{{moreUrl}}}">{{default moreText "More"}}</a>
            </div>
          </div>
        {{/if}}
      {{/if}}
    </div>
  </div>
{{/ifAll}}

partials.search_info:
{{#*inline "current_page" ~}}
{{#compare paging.totalResults ">" 10 ~}} |
<strong>Current page:</strong> {{addCommas paging.currentPage}} of {{addCommas paging.totalPages}}
{{/compare ~}}
{{/inline ~}}

{{#if paging.totalResults ~}}
<div class="search-info mdc-elevation--z2">
  <strong>Total results:</strong> {{addCommas paging.totalResults}}
  {{#ifAny @root.request.params.query.keywords @root.request.params.query.activities @root.request.params.query.beneficiaries @root.request.params.query.geographical_areas_funded @root.request.params.query.type_of_cost @root.request.params.query.type_of_funding @root.request.params.query.source_of_fund @root.request.params.query.miscellaneous @root.request.params.query.page ~}}
  {{#ifAll (compare paging.totalResults ">" 50) (compare @root.route.name "!==" "posts") ~}}
  (limited to 50 per search)
  {{else}}
  {{> current_page}}
  {{/ifAll ~}}
  {{else}}
  {{> current_page}}
  {{/ifAny ~}}
</div>
{{else}}
{{#ifAny @root.request.params.query.keywords @root.request.params.query.activities @root.request.params.query.beneficiaries @root.request.params.query.geographical_areas_funded @root.request.params.query.type_of_cost @root.request.params.query.type_of_funding @root.request.params.query.source_of_fund @root.request.params.query.miscellaneous ~}}
<div class="search-info mdc-elevation--z1">
  No results could be found, please try a different search query
</div>
{{/ifAny ~}}
{{/if ~}}

templates.pages_page-head-tags:
{{#if data.primaryResponse.hits.hits.[0] ~}}
{{#with data.primaryResponse.hits.hits.[0]._source ~}}
{{#ifAny title og_title ~}}
<title>{{@root.data.currentSite.title}} – {{{default title og_title}}}</title>
<meta property="og:title" content="{{@root.data.currentSite.title}} – {{{default title og_title}}}" data-dynamic="true">
{{else}}
<title>{{@root.data.currentSite.title}}</title>
<meta property="og:title" content="{{@root.data.currentSite.title}}" data-dynamic="true">
{{/ifAny ~}}
<!-- <meta property="og:image" content="" data-dynamic="true"> -->
<meta property="og:image:width" content="96" data-dynamic="true">
<meta property="og:image:height" content="96" data-dynamic="true">
{{#ifAny og_summary description ../og_summary ../description ~}}
<meta property="og:summary" content="{{stripTags (regexReplace (default og_summary (default ../og_summary (default description ../description))) '\r|\n' 'gi' '')}}" data-dynamic="true">
<meta name="description" content="{{stripTags (regexReplace (default description (default ../description (default og_summary ../og_summary))) '\r|\n' 'gi' '')}}" data-dynamic="true">
{{/ifAny ~}}
{{/with ~}}
{{else}}
<title>{{@root.data.currentSite.title}}</title>
<meta property="og:title" content="{{@root.data.currentSite.title}}" data-dynamic="true">
<meta property="og:summary" content="{{@root.data.currentSite.title}} lists funders with a track record of supporting projects in Scotland and is run by the Scottish Council for Voluntary Organisations" data-dynamic="true">
<meta name="description" content="{{@root.data.currentSite.title}} lists funders with a track record of supporting projects in Scotland and is run by the Scottish Council for Voluntary Organisations" data-dynamic="true">
{{/if ~}}

templates.pages_page-map:
{{#*inline "marker"}}
<marker style="display:none;" data-map="fullMap" data-lat="{{lat}}" data-lng="{{lng}}" data-title="{{source.title}}">
  <div class="map-content">
    {{#with source ~}}
    <h3 class="mdc-typography--headline7">
      {{{title}}}
    </h3>
    <div class="mdc-typography--body2">
      {{{content}}}
    </div>
    <div class="mdc-typography--body2">
      <ul>
        {{#if address ~}}
        <li><strong>Address:</strong> {{address}}</li>
        {{/if ~}}
        {{#if phone_number ~}}
        <li><strong>Phone:</strong> {{phone_number}}</li>
        {{/if ~}}
        {{#if email_address ~}}
        <li><strong>Email:</strong> <a href="mailto:{{email_address}}">{{email_address}}</a></li>
        {{/if ~}}
        {{#if website_address ~}}
        <li><strong>Website:</strong> <a href="{{fixUrl website_address}}">{{website_address}}</a></li>
        {{/if ~}}
      </ul>
    </div>
    {{/with ~}}
  </div>
</marker>
{{/inline}}

<div class="container-content mpower-page t-padding">
  <div class="b-padding-full t-margin b-margin-full">
    {{#compare @root.data.primaryResponse.hits.total ">" 0 ~}}
    <h1 class="mdc-typography--headline4">
      {{{@root.data.primaryResponse.hits.hits.[0]._source.title}}}
    </h1>
    {{{@root.data.primaryResponse.hits.hits.[0]._source.content}}}

    <div id="map-full" data-map-name="fullMap" data-map-clustered-options='{}'>
      {{#each @root.data.supplimentaryResponses.organisations.hits.hits ~}}
        {{#each (arrayify _source.geo_coords) ~}}
          {{> marker lat=lat lng=lon source=../_source index=@index}}
        {{/each ~}}
      {{/each ~}}
    </div>

    {{else}}
    <h2 class="mdc-typography--headline5">
      Error 404: Page not found
    </h2>
    {{/compare ~}}
  </div>
</div>

