                  {{/json}}
                {{/json}},
                {{#json}}
                  {{#json key="exists"}}
                    {{{json key="field" value="eircode"}}}
                  {{/json}}
                {{/json}}
              {{/json}},
              {{{json key="minimum_should_match" value=1}}}
